mapper.select:
SELECT qnaid, title, content, memberid, cdate, viewcnt
FROM m_QnA
WHERE qnaid = #{qnaid}

SELECT
COUNT(refno) FROM m_QnA
WHERE refno = #{qnaid}

mapper.update:
UPDATE m_QnA 
SET title=#{title}, content=#{content}
WHERE qnaid = #{qnaid}

UPDATE m_QnA 
SET viewcnt = viewcnt + 1 
WHERE qnaid = #{qnaid}

UPDATE m_QnA
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
